#include <stdio.h>
#include <driver/i2c.h>
#include <freertos/FreeRTOS.h>
#include <freertos/task.h>
#include <esp_log.h>
#include <esp_task_wdt.h>

#define I2S_NUM         I2S_NUM_0
#define SAMPLE_RATE     44100
#define I2S_BCLK        26
#define I2S_LRC         27
#define I2S_DOUT        25
#define PI              3.14159265358979323846
#define SINE_WAVE_FREQ  440  // 440Hz (A4 note)
#define AMPLITUDE       2000
#define BUFFER_SIZE     512

void i2s_init(void) {
    i2s_config_t i2s_config = {
        .mode = I2S_MODE_MASTER | I2S_MODE_TX,
        .sample_rate = SAMPLE_RATE,
        .bits_per_sample = I2S_BITS_PER_SAMPLE_16BIT,
        .channel_format = I2S_CHANNEL_FMT_RIGHT_LEFT,
        .communication_format = I2S_COMM_FORMAT_I2S,
        .intr_alloc_flags = ESP_INTR_FLAG_LEVEL1,
        .dma_buf_count = 8,
        .dma_buf_len = BUFFER_SIZE,
        .use_apll = false,
        .tx_desc_auto_clear = true,
    };

    i2s_pin_config_t pin_config = {
        .bck_io_num = I2S_BCLK,
        .ws_io_num = I2S_LRC,
        .data_out_num = I2S_DOUT,
        .data_in_num = I2S_PIN_NO_CHANGE
    };

    i2s_driver_install(I2S_NUM, &i2s_config, 0, NULL);
    i2s_set_pin(I2S_NUM, &pin_config);
    i2s_zero_dma_buffer(I2S_NUM);
}

void generate_sine_wave() {
    int16_t samples[BUFFER_SIZE];
    size_t bytes_written;

    for (int i = 0; i < BUFFER_SIZE / 2; i++) {
        float theta = 2.0 * PI * SINE_WAVE_FREQ * i / SAMPLE_RATE;
        int16_t sample_value = (int16_t)(AMPLITUDE * sin(theta));

        samples[i * 2] = sample_value;      // Left channel
        samples[i * 2 + 1] = sample_value;  // Right channel
    }

    while (1) {
        i2s_write(I2S_NUM, samples, sizeof(samples), &bytes_written, portMAX_DELAY);
    }
}

void app_main(void) {
    i2s_init();
    generate_sine_wave();
}
